INSERT INTO `sejarah` (`id`, `text`, `bg`, `tgl`) VALUES
(1, '<p style="text-align:justify">SMA Negeri 1 Taman berdiri pada bulan Juli 1984 sesuai dengan S.K. Mendikbud RI No.: 0558/0/1984 dengan nama SMA Negeri 1 Taman (Smanita) pada waktu itu menempati gedung SMP Negeri 1 Taman di Jalan Satria 1 Ketegan, Taman sampai dengan tanggal 19 Nopember 1986 dan baru pada tanggal 20 Nopember 1986 menempati gedung baru di Jalan Sawunggaling 2 Jemundo Taman sampai sekarang,dan kemudian dengan S.K. Mendikbud RI No.: 035/0/1997 SMA Negeri 1 Taman berubah menjadi SMU Negeri 1 Taman. Sejalan dengan perkembangan kurikulum perubahan nama itu tidaklah bertahan cukup lama, karena pada tahun 2001 menyongsong lahirnya kurikulum 2004 nama SMU Negeri 1 Taman kembali menjadi SMA Negeri 1 Taman sampai sekarang.97 Gedung SMA Negeri 1 Taman berlokasi di Jalan Sawunggaling 2 desa Jemundo, Taman, dengan luas areal 8000 m2 memiliki ruang kelas berjumlah 27 kelas/rombongan belajar, sebuah ruang kepala sekolah, sebuah ruang wakil kepala sekolah, sebuah ruang Bimbingan Konseling, dan 5 buah ruang laboratorium yaitu: Laboratorium Fisika, Laboratorium Kimia, Laboratorium Biologi, laboratorium Teknologi Informasi, laboratorium Bahasa, sebuah ruang Perpustakaan, sebuah ruang Tata Usaha, sebuah tempat ibadah yaitu masjid Baitul Ilmi SMA Negeri 1 Taman, 50 kran air untuk wudlu dan cuci, 1 kamar mandi kepala sekolah, 2 kamar mandi guru/TU, 12 (dua belas) kamar mandi siswa dan beberapa fasilitas penunjang lainnya. SMA Negeri 1Taman pada saat penulis mengadakan penelitian mempunyai 57 tenaga pengajar dengan status PNS, 3 pengajar GTT, 5 pegawai tetap, 1 pegawai tidak tetap dan 3 pesuruh.</p>\r\n', 'img/gal3c.jpg', '2019-08-06 05:58:38');

